import React from "react";
import useSWR from "swr";
import { Container, Title, RowContainer } from "../ui-kit";
import { BulletList as Loader } from "react-content-loader";
import styled from "@emotion/styled";
import { Todo } from "../utils/types";
import { TodoList } from "../components/TodoList";

export const HomeContainer = styled.div`
  // backdrop-filter: blur(300px) contrast(0.8);
  font-family: ${({ theme }) => theme.fontFamily.heading};
  padding: 10px 2vw;
  margin: 0.25vw 0 0.75vw 0;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  transition: ${({ theme }) => theme.transitions.boom.transition};
  svg {
    width: 40vw;
  }
  @media (max-width: ${({ theme }) => theme.breakpoints.s}) {
    font-size: 1rem;
    input {
      width: 75vw;
    }
  }
  border-radius: ${({ theme }) => theme.borderRadius.default};
  box-shadow: ${({ theme }) => theme.shadow.mekari};
  text {
    margin-top: calc(0.3rem + 0.2vw);
  }
  .submit {
    cursor: pointer;
    &:hover {
      background: ${({ theme }) => theme.colors.snazzy.mekariPurple};
      color: ${({ theme }) => theme.colors.white.base};
    }
    font-weight: bold;
  }
  white-space: pre-line;
  word-break: break-all;
  overflow-wrap: break-word;
  div {
    margin: 0 5px;
  }
`;
export const Home: React.FC = () => {
  const URL = "http://localhost:8000";
  const fetcher = async (endpoint: string) => {
    const response = await fetch(`${URL}/${endpoint}/`);
    const json = await response.json();
    return json;
  };
  const { data, error, mutate } = useSWR<[Todo]>("todos", fetcher);
  const addTodo = async (name: string) => {
    await fetch(`${URL}/todos/`, {
      method: "POST",
      body: JSON.stringify({ name }),
    });
    mutate({ ...data! });
  };
  const deleteTodo = async (id: number) => {
    console.log(id);
    // await fetch(`${URL}/todos/${id}/`);
    mutate({ ...data! });
  };
  const updateTodo = async (id: number) => {
    console.log(id);
    // await fetch(`${URL}/todos/${id}/`);
    mutate({ ...data! });
  };
  return (
    <>
      <HomeContainer>
        {!data ? (
          <Loader />
        ) : (
          <>
            <TodoList todo={data!} />
          </>
        )}
      </HomeContainer>
    </>
  );
};
