import styled from "@emotion/styled";

type Cont = {
  closed?: boolean;
};

export const Container = styled.div<Cont>`
  background: ${({ theme }) => theme.colors.white.base};
  font-family: ${({ theme }) => theme.fontFamily.heading};
  padding: 10px 2vw;
  margin: 0.25vw 0 0.75vw 0;
  display: ${(props) => (props.closed ? "none" : "flex")};
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  transition: ${({ theme }) => theme.transitions.boom.transition};
  svg {
    width: 40vw;
  }
  @media (max-width: ${({ theme }) => theme.breakpoints.s}) {
    font-size: 1rem;
    input {
      width: 75vw;
    }
  }
  border-radius: ${({ theme }) => theme.borderRadius.default};
  box-shadow: ${({ theme }) => theme.shadow.mekari};
  text {
    margin-top: calc(0.3rem + 0.2vw);
  }
  .submit {
    cursor: pointer;
    &:hover {
      background: ${({ theme }) => theme.colors.snazzy.mekariPurple};
      color: ${({ theme }) => theme.colors.white.base};
    }
    font-weight: bold;
  }
  white-space: pre-line;
  word-break: break-all;
  overflow-wrap: break-word;
  div {
    margin: 0 5px;
  }
`;
export const GoogleButton = styled.button`
  transition: ${(props) => props.theme.transitions.boom.transition};
  font-weight: bold;
  color: ${({ theme }) => theme.colors.white.base};
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid #e2e2e2;
  cursor: pointer;
  border-radius: 2px;
  // margin: 0 0.2vw;
  padding: calc(5px + 0.2vw);
  &:focus {
    outline: 0;
  }
  &:hover {
    transform: scale(1.03);
  }
  border: none;
  font-size: calc(0.15vw + 0.7rem);
`;

export const Title = styled.h1`
  display: flex;
  color: ${({ theme }) => theme.colors.background.dlight};
  font-size: calc(0.3vw + 0.8rem);
  word-break: break-word;
`;
export const SubTitle = styled.h2`
  display: inline-block;
  // display: flex;
  color: ${(props) =>
    props.color === "PURPLE"
      ? ({ theme }) => theme.colors.snazzy.mekariPurple
      : ({ theme }) => theme.colors.background.dlight};
  font-size: calc(0.3vw + 0.7rem);
  word-break: break-word;
`;

export const SubSubTitle = styled.h3`
  display: inline-block;
  color: ${({ theme }) => theme.colors.background.dlight};
  font-size: calc(0.2vw + 0.6rem);
  word-break: break-word;
`;
