import React, { useContext } from "react";
import { Container, RowContainer, Button } from "../../ui-kit";
import { UserContext } from "../../datastore/UserContext";
import { AUTH_TOKEN_STORAGE_KEY, GOOGLE_CLIENT_ID } from "../../constants";
import styled from "@emotion/styled/";
import { Instagram } from "react-content-loader";

interface HeaderProps {}

const Nav = styled.nav`
  backdrop-filter: blur(30px);
`;

const Wrapper = styled.div`
  margin: 15px 20vw;
  @media (max-width: ${({ theme }) => theme.breakpoints.s}) {
    margin: 10px;
  }
  transition: ${({ theme }) => theme.transitions.boom.transition};
  color: ${({ theme }) => theme.colors.white.base};
  font-weight: bold;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Header: React.FC<HeaderProps> = ({ children }) => {
  const { user, setUser } = useContext(UserContext);
  const logout = () => {
    console.log("yo");
    localStorage.setItem(AUTH_TOKEN_STORAGE_KEY, "");
    setUser(null);
  };
  const responseGoogle = (response: any) => {
    console.log(response);
  };
  return (
    <Container>
      <Nav>
        <RowContainer>
          {!user ? (
            <Instagram />
          ) : (
            <img src={user!.picture} alt="google-profile"></img>
          )}
          <Button onClick={logout} color="RED">
            Logout
          </Button>
        </RowContainer>
      </Nav>
      <Wrapper>{children}</Wrapper>
    </Container>
  );
};
