import React, { useContext, useState } from "react";
import { Container, RowContainer, Button } from "../../ui-kit";
import { UserContext } from "../../datastore/UserContext";

import styled from "@emotion/styled/";
import { Instagram } from "react-content-loader";

interface HeaderProps {
  logout: any;
}

const Nav = styled.nav`
  // width: 100vw;
  backdrop-filter: blur(30px);
  img {
    cursor: pointer;
  }
  padding: 10px;
  position: fixed;
  // background: white;
  z-index: 2;
  img {
    width: calc(0.5vw + 2rem);
    border-radius: ${({ theme }) => theme.borderRadius.default};
  }
`;

const Wrapper = styled.div`
  // margin: 15px 200vw;
  margin-top: 50px;
  // @media (max-width: ${({ theme }) => theme.breakpoints.s}) {
  //   margin-top: 50px;
  // }
  transition: ${({ theme }) => theme.transitions.boom.transition};
  color: ${({ theme }) => theme.colors.white.base};
  font-weight: bold;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Header: React.FC<HeaderProps> = ({ children, logout }) => {
  const { user } = useContext(UserContext);
  console.log(user!.picture);
  const [modal, setModal] = useState(false);
  return (
    <>
      <Nav>
        <RowContainer>
          {!user! ? (
            <Instagram />
          ) : (
            <img
              onClick={() => setModal((v) => !v)}
              src={user!.picture}
              alt="google-profile"
            ></img>
          )}
          {modal && (
            <Button onClick={logout} color="RED">
              Logout
            </Button>
          )}
        </RowContainer>
      </Nav>
      <Wrapper>{children}</Wrapper>
    </>
  );
};
