import React from "react";
import { TodoType } from "../../utils/types";
import { Container, RowContainer, Title, Button } from "../../ui-kit";

interface TodoProps {
  todo: TodoType;
  updateTodo: any;
  deleteTodo: any;
}

export const Todo: React.FC<TodoProps> = ({ todo, updateTodo, deleteTodo }) => {
  return (
    <Container>
      <RowContainer>
        <Button onClick={() => updateTodo(todo.id, todo.name, !todo.completed)}>
          Done
        </Button>
        <Title>{todo.completed ? <s>{todo.name}</s> : todo.name}</Title>
        <Button color="RED" onClick={() => deleteTodo(todo.id)}>
          Delete
        </Button>
      </RowContainer>
    </Container>
  );
};
