import React, { useState, useCallback } from "react";
import { BrowserRouter, Switch, Route } from "react-router-dom";
import { theme } from "../utils/theme";
import { css, Global, ThemeProvider } from "@emotion/react";
import "typeface-overpass";
import { Home } from "../pages";
import styled from "@emotion/styled";
import GoogleLogin from "react-google-login";
import { AUTH_TOKEN_STORAGE_KEY } from "../constants";

const Wrapper = styled.div`
  margin: 15px 20vw;
  @media (max-width: ${({ theme }) => theme.breakpoints.s}) {
    margin: 10px;
  }
  transition: ${({ theme }) => theme.transitions.boom.transition};
  color: ${({ theme }) => theme.colors.white.base};
  font-weight: bold;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
`;
export const Routes: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(
    Boolean(localStorage.getItem(AUTH_TOKEN_STORAGE_KEY))
  );

  const refreshAuthStatus = useCallback(() => {
    setIsAuthenticated(Boolean(localStorage.getItem(AUTH_TOKEN_STORAGE_KEY)));
  }, []);

  const login = useCallback(
    (token) => {
      console.log(token);
      localStorage.setItem(AUTH_TOKEN_STORAGE_KEY, token);
      refreshAuthStatus();
    },
    [refreshAuthStatus]
  );
  const responseGoogle = (response: any) => {
    console.log(response);
  };
  return (
    <ThemeProvider theme={theme}>
      <Global
        styles={css`
          *,
          *:before,
          *:after {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
          }
          html {
            text-rendering: optimizeLegibility;
            overflow-x: hidden;
            -ms-overflow-style: scrollbar;
            -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;
          }
          body {
            display: flex;
            flex-direction: column;
            background: ${theme.colors.white.background};
            font-family: ${theme.fontFamily.body};
            background-color: #8ec5fc;
            background-image: linear-gradient(62deg, #8ec5fc 0%, #e0c3fc 100%);
          }
          h1 {
            font-family: ${theme.fontFamily.heading};
          }
          input {
            border: none;
          }
          button {
            cursor: pointer;
          }
          textarea:focus,
          button:focus,
          input:focus {
            outline: none;
          }
        `}
      />
      <BrowserRouter>
        <Wrapper>
          {!sAuthenticated ? (
            <Switch>
              <Route exact path="/" component={Home} />
            </Switch>
          ) : (
            <GoogleLogin
              clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID!}
              buttonText="Login with Google"
              onSuccess={(token) => login(token)}
              onFailure={responseGoogle}
              cookiePolicy={"single_host_origin"}
            />
          )}
        </Wrapper>
      </BrowserRouter>
    </ThemeProvider>
  );
};
